mapper.select:
/* iboard, iuser, regdt, 글쓴이 이름 정렬은 최신글이 가장 위로나오도록 */ SELECT A.iboard, A.title, A.iuser, A.regdt ,B.unm AS writerNm, B.profileImg , CASE WHEN C.iboard IS NULL THEN 0 ELSE 1 END AS isFav FROM t_board A INNER JOIN t_user B ON A.iuser = B.iuser
LEFT JOIN t_board_fav C
INNER JOIN t_board_fav C
ON A.iboard = C.iboard AND C.iuser = #{iuser} ORDER BY A.iboard DESC
LIMIT #{startIdx}, #{recordCnt};

mapper.update:
UPDATE t_board
SET title = #{title}
  , ctnt = #{ctnt}
WHERE iboard = #{iboard}
  AND iuser = #{iuser}

mapper.delete:
DELETE FROM t_board
WHERE iboard = #{iboard}
  AND iuser = #{iuser}
